import React, { Component } from "react";

import Block from "../block/block.component";
import Cnn from "../../img/cnn.jpeg";
import Goal from "../../img/goal.jpeg";
import Sci from "../../img/scihub.png";
import Dstv from "../../img/dstv.jpeg";
import Audio from "../../img/admack.jpeg";
import Git from "../../img/cng";
import "./hot.style.scss";

class Hot extends Component {
  constructor() {
    super();

    this.state = {
      sections: [
        {
          id: 1,
          title: "hot1",
          bgImg:  Cnn ,
        },
        {
          id: 2,
          title: "hot2",
          bgImg: Dstv,
        },
        {
          id: 3,
          title: "hot3",
          bgImg: Git,
        },
        {
          id: 4,
          title: "hot4",
          bgImg: Goal,
        },
        {
          id: 5,
          title: "hot5",
          bgImg: Audio
        },
        {
          id: 6,
          title: "hot6",
          bgImg: Sci
        },
      ],
    };
  }

  render() {
    return (
      <div className='box'>
        {this.state.sections.map(({ id, title, bgImg }) => (
          <Block key={id} title={title} bgImg={bgImg} />
        ))}
      </div>
    );
  }
}

export default Hot;
