import React, { Component } from "react";

import Block from "../block/block.component";
import Jamb from "../../img/cnn.jpeg";
import "./hot.style.scss";

class Hot extends Component {
  constructor() {
    super();

    this.state = {
      sections: [
        {
          id: 1,
          title: "hot1",
          bgImg: "Jamb",
        },
        {
          id: 2,
          title: "hot2",
          bgImg: [
            {
              img: "{Box}",
            },
          ],
        },
        {
          id: 3,
          title: "hot3",
          bgImg: [
            {
              img: "{Box}",
            },
          ],
        },
        {
          id: 4,
          title: "hot4",
          bgImg: [
            {
              img: "{Box}",
            },
          ],
        },
        {
          id: 5,
          title: "hot5",
          bgImg: [
            {
              img: "{Box}",
            },
          ],
        },
        {
          id: 6,
          title: "hot6",
          bgImg: [
            {
              img: "{Box}",
            },
          ],
        },
      ],
    };
  }

  render() {
    return (
      <div className='box'>
        {this.state.sections.map(({ id, title, bgImg }) => (
          <Block key={id} title={title} bgImg={bgImg} />
        ))}
      </div>
    );
  }
}

export default Hot;
